import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';

const WebcamCapture = () => {
  const webcamRef = useRef(null);
  const [name, setName] = useState('');
  const [mode, setMode] = useState('attendance'); // 'register' or 'attendance'
  const [message, setMessage] = useState('');

  const captureAndSend = async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    if (!imageSrc) {
      setMessage('‚ùå Unable to capture image. Please try again.');
      return;
    }

    const blob = await (await fetch(imageSrc)).blob();
    const formData = new FormData();
    formData.append('image', blob, 'face.jpg');
    if (mode === 'register') formData.append('name', name);

    try {
      const endpoint = mode === 'register' ? '/register/' : '/attendance/';
      const response = await axios.post(endpoint, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (mode === 'register') {
        setMessage(`‚úÖ Registered: ${response.data.name} (ID: ${response.data.employee_id})`);
      } else {
        setMessage(`‚úÖ Attendance marked for ${response.data.employee} (Confidence: ${response.data.confidence})`);
      }
    } catch (error) {
      const errMsg = error.response?.data?.error || 'Server error. Try again.';
      setMessage(`‚ùå ${errMsg}`);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>{mode === 'register' ? 'Face Registration' : 'Mark Attendance'}</h2>
      <Webcam
        audio={false}
        ref={webcamRef}
        screenshotFormat="image/jpeg"
        width={320}
        height={240}
        videoConstraints={{ facingMode: 'user' }}
      />
      {mode === 'register' && (
        <input
          type="text"
          placeholder="Enter name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ marginTop: '10px', padding: '5px' }}
        />
      )}
      <div style={{ marginTop: '10px' }}>
        <button onClick={captureAndSend}>üì∑ Capture & Submit</button>
        <button onClick={() => setMode(mode === 'register' ? 'attendance' : 'register')} style={{ marginLeft: '10px' }}>
          üîÑ Switch to {mode === 'register' ? 'Attendance' : 'Register'}
        </button>
      </div>
      <p style={{ marginTop: '20px', fontWeight: 'bold' }}>{message}</p>
    </div>
  );
};

export default WebcamCapture;
